import React from 'react';
import {Component} from 'react'
import './App.css';
import Person from './Person/person'

class App extends Component{

  state={

    people:[{p_name:'amit Yadav',p_age:'30'},{p_name:'shweta Yadav',p_age:'32'},{p_name:'usha',p_age:'34'}],

    people_info:'belong to same family ',
    showpeople:false,
  }

  switchnameHandler = () => {
      //console.log('was clicked')
      this.setState({

        people:[{p_name:'amit Yadav',p_age:'30'},{p_name:'shweta Yadav allahabadi',p_age:'19'},{p_name:'usha',p_age:'34'}],

        people_info:'belong to same family'
      })
  }

  togglepeopleHandler = () => {
    const doesShow = this.state.showpeople ;
    this.setState({showpeople : !doesShow})
  }


  render(){

    let person_list=null;
    if (this.state.showpeople)
    {
      person_list=(
        <div>
          {
            this.state.people.map((argument) => {
              return <Person name ={argument.p_name}
                            age ={argument.p_age}/>
            })
          }

        </div> 
      );
    }

    return (
      <div className="App">
        <h1>helloamit React js</h1>
        <button onClick={this.togglepeopleHandler}>click here</button>
        {person_list}

      </div>
    );
  }
}

export default App;
