import React from 'react';
import {Component} from 'react'
import './App.css';
import Person from './Person/person'

class App extends Component{

  state={

    people:[{p_name:'amit Yadav',p_age:'30'},{p_name:'shweta Yadav',p_age:'32'},{p_name:'usha',p_age:'34'}],

    people_info:'belong to same family '
  }

  switchnameHandler = () => {
      //console.log('was clicked')
      this.setState({

        people:[{p_name:'amit Yadav',p_age:'30'},{p_name:'shweta Yadav',p_age:'32'},{p_name:'usha',p_age:'34'}],

        people_info:'belong to same family '
      })
  }


  render(){
    return (
      <div className="App">
        <h1>helloamit React js</h1>
        <button onClick={this.switchnameHandler}>click here</button>
        <Person 
          name={this.state.people[0].p_name} 
          age={this.state.people[0].p_age}>

        </Person>
        <Person 
          name={this.state.people[1].p_name} 
          age={this.state.people[1].p_age}>

        </Person>
        <Person 
          name={this.state.people[2].p_name} 
          age={this.state.people[2].p_age}>
          from Allahabad
        </Person>

      </div>
    );
  }
}

export default App;
