import React from 'react';
import {Component} from 'react'
import './App.css';
// import Person from '../components/persons/Person/person'

import Persons from '../components/persons/persons'

class App extends Component{

  constructor(props)
  {
    console.log('App.js constructor')
    super(props);
    this.state ={

      people:[{p_name:'amit Yadav',p_age:'30',id :1,},{p_name:'shweta Yadav',p_age:'32',id:2},{p_name:'usha',p_age:'34',id:3}],

      people_info:'belong to same family ',
      showpeople:false,

    }
  }
  // use inside constructor or decleare globally

  // state ={

  //   people:[{p_name:'amit Yadav',p_age:'30',id :1,},{p_name:'shweta Yadav',p_age:'32',id:2},{p_name:'usha',p_age:'34',id:3}],

  //   people_info:'belong to same family ',
  //   showpeople:false,

  // }


  static getDerivedStateFromProps(props, state){
    console.log('App.js GetDerivedStateFromProps')

    return state
  }

  componentDidMount(){
    console.log('App.js componentDidMount')
    return 
  }

  shouldComponentUpdate(nextProps,nextState){
    console.log('App.js should componentUpdate')
    return true
  }
  componentDidUpdate(){

    console.log('App.js componentDidUpdate')

  }


  togglepeopleHandler = () => {
    const doesShow = this.state.showpeople ;
    this.setState({showpeople : !doesShow})
  }

  deletepeopleHandler = (people_index) => {
    const del_people = [...this.state.people]
    del_people.splice(people_index,1)
    this.setState({people:del_people})
  }

  render(){
    console.log('App.js render')
    let person_list=null;
    if (this.state.showpeople)
    {
      person_list=(
        <div>
          <Persons people={this.state.people}
                  clicked ={this.deletepeopleHandler}/>


        </div> 
      );
    }

    return (
      <div className="App">
        <h1>helloamit React js</h1>
        <button onClick={this.togglepeopleHandler}>click here</button>
        {person_list}

      </div>
    );
  }
}

export default App;
